        if(!this.pwd) {
          if(!this.password) {
           Toast('请输入密码')
           return ''
          } else {
           pwds = md5(this.password)
          }
        } else {
         pwds = this.pwd
        }
        //this.regPhone.test(this.phone)
        // console.log(!this.phone,pwds)
        if(!this.phone) {
          Toast('请输入手机号或企业名')
          return ''
        } else if(pwds == 'd41d8cd98f00b204e9800998ecf8427e') {
          Toast('请输入密码')
          return ''
        } else {
         const params = JSON.stringify({
            customerLoginName: this.phone,
            customerLoginPwd: pwds,
            wxOpenId: this.openId
          })
//				 const token = this.formId ? {
//					 headers:{
//						'FormID': this.formId,
//					  'AppID': this.openId
//					}
//				 }: {};
//         console.log(token, this.formId);
				this.$http.post('/customer/logon',params).then((res)=> {
              let data = res.data
              if(data.resultCode == '0000000') {
                Toast.clear()
                Util.setStorage('userInfo', data.returnData)
                Util.redTo('../index/main')
              } else {
                Toast(data.resultDesc)
              }
          })
        }
    }
    ,toRegister() {
      Util.navTo('../register/main?id=1')
    }
    ,autoLogin() {
       let userInfo = this.userInfo
       if(userInfo) {         
          this.phone = userInfo.customerLinkTel ? userInfo.customerLinkTel : userInfo.customerLoginName
           this.pwd = userInfo.customerLoginPwd
         Toast({
           duration: 0,
           message:'自动登录中...',
           mask: true
         })
         setTimeout(()=> {
          this.onLogin()
         },2500)
          
       }
    }
